var querystring = require("querystring")

function iniciar(pResponse, pDataReceived) {

    var body = '<html>'+
    '<head>'+
    '<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />'+
    '</head>'+
    '<body>'+
    '<form action="/subir" method="post">'+
    '<textarea name="text" rows="20" cols="60"></textarea>'+
    '<input type="submit" value="Submit text" />'+
    '</form>'+
    '</body>'+
    '</html>';

    pResponse.writeHead(200, {"Content-Type": "text/html"});
    pResponse.write(body);
    pResponse.end();

}

function subir(pResponse) {
  //console.log("Manipulador de peticion 'subir' fue llamado.");
    pResponse.writeHead(404, {"Content-Type": "text/html"});
    pResponse.write("Hola Subir");
    pResponse.end();
}

exports.iniciar = iniciar;
exports.subir = subir;