function route(pHandle, pPathName, pResponse, pDataReceived) {
    if (typeof pHandle[pPathName] === 'function') {
        return pHandle[pPathName](pResponse, pDataReceived);
    } else {
        pResponse.writeHead(404, {"Content-Type": "text/html"});
        pResponse.write("404 Not found");
        pResponse.end();
    }
}
exports.route = route;